    }

    <span class="hljs-keyword">public</span> <span class="hljs-function"><span class="hljs-keyword">function</span> <span class="hljs-title">authenticate</span>(<span class="hljs-params"><span class="hljs-keyword">string</span> <span class="hljs-variable">$username</span>, <span class="hljs-keyword">string</span> <span class="hljs-variable">$password</span></span>): <span class="hljs-title">SimpleIdentity</span>
    </span>{
        <span class="hljs-variable">$row</span> = <span class="hljs-variable language_">$this</span>-&gt;database-&gt;<span class="hljs-title function_ invoke__">table</span>(<span class="hljs-string">'users'</span>)
            -&gt;<span class="hljs-title function_ invoke__">where</span>(<span class="hljs-string">'username'</span>, <span class="hljs-variable">$username</span>)
            -&gt;<span class="hljs-title function_ invoke__">fetch</span>();

        <span class="hljs-keyword">if</span> (!<span class="hljs-variable">$row</span>) {
            <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> <span class="hljs-title class_">Nette\Security\AuthenticationException</span>(<span class="hljs-string">'User not found.'</span>);
        }

        <span class="hljs-keyword">if</span> (!<span class="hljs-title class_">Nette\Security\Passwords</span>::<span class="hljs-title function_ invoke__">verify</span>(<span class="hljs-variable">$password</span>, <span class="hljs-variable">$row</span>-&gt;password)) {
            <span class="hljs-keyword">throw</span> <span class="hljs-keyword">new</span> <span class="hljs-title class_">Nette\Security\AuthenticationException</span>(<span class="hljs-string">'Invalid password.'</span>);
        }

        <span class="hljs-keyword">return</span> <span class="hljs-keyword">new</span> <span class="hljs-title class_">SimpleIdentity</span>(
            <span class="hljs-variable">$row</span>-&gt;id,
            <span class="hljs-variable">$row</span>-&gt;role,
            [<span class="hljs-string">'username'</span> =&gt; <span class="hljs-variable">$row</span>-&gt;username]
        );
    }
}
</code></div></div></pre><h3><strong>Step 3: Register the UserManager as a Service</strong></h3><p>In <code>config/common.neon</code>:</p><pre class="!overflow-visible"><div class="dark bg-gray-950 contain-inline-size rounded-md border-[0.5px] border-token-border-medium relative"><div class="flex items-center text-token-text-secondary bg-token-main-surface-secondary px-4 py-2 text-xs font-sans justify-between rounded-t-md h-9">neon</div><div class="sticky top-9 md:top-[5.75rem]"><div class="absolute bottom-0 right-2 flex h-9 items-center"><div class="flex items-center rounded bg-token-main-surface-secondary px-2 font-sans text-xs text-token-text-secondary"><span class="" data-state="closed"><button class="flex gap-1 items-center py-1"><svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" class="icon-sm"><path fill-rule="evenodd" clip-rule="evenodd" d="M7 5C7 3.34315 8.34315 2 10 2H19C20.6569 2 22 3.34315 22 5V14C22 15.6569 20.6569 17 19 17H17V19C17 20.6569 15.6569 22 14 22H5C3.34315 22 2 20.6569 2 19V10C2 8.34315 3.34315 7 5 7H7V5ZM9 7H14C15.6569 7 17 8.34315 17 10V15H19C19.5523 15 20 14.5523 20 14V5C20 4.44772 19.5523 4 19 4H10C9.44772 4 9 4.44772 9 5V7ZM5 9C4.44772 9 4 9.44772 4 10V19C4 19.5523 4.44772 20 5 20H14C14.5523 20 15 19.5523 15 19V10C15 9.44772 14.5523 9 14 9H5Z" fill="currentColor"></path></svg>Copy code</button></span></div></div></div><div class="overflow-y-auto p-4" dir="ltr"><code class="!whitespace-pre hljs language-neon">services:
