        <span class="hljs-variable">$tools</span> = <span class="hljs-variable language_">$this</span>-&gt;database-&gt;<span class="hljs-title function_ invoke__">table</span>(<span class="hljs-string">'tools'</span>)-&gt;<span class="hljs-title function_ invoke__">where</span>(<span class="hljs-string">'quantity &gt; ?'</span>, <span class="hljs-number">0</span>)-&gt;<span class="hljs-title function_ invoke__">fetchPairs</span>(<span class="hljs-string">'id'</span>, <span class="hljs-string">'name'</span>);

